    printf("Thread data: %s\n", (char*)pthread_getspecific(key));
    return NULL;
}

int main() {
    pthread_key_create(&key, destructor);
    pthread_t thread;
    char* msg = "Hello Thread";
    pthread_create(&thread, NULL, thread_func, msg);
    pthread_join(thread, NULL);
    pthread_key_delete(key);
    return 0;
}

